/**
 * Hobbit ellenség.
 */
public class Hobbit extends Enemy
{
	/**
	 * Konstruktor.
	 * @param hp  Életerõ.
	 * @param as  Az aktuális sebesség.
	 * @param os  Az eredeti, lassítás nélküli sebesség.
	 * @param m  Az ellenség halálakor szarumánhoz kerülõ varázserõ.
	 * @param lt  Az az idõpont amikor az ellenség legutoljára lépett.
	 */
	public Hobbit(int hp, int as, int os, int m, long lt) {
		super(hp, as, os, m, lt);
	}

	public boolean damage(int power, HashMap<String, Integer> bonus)
	{
		return true;
	}

	public void move()
	{
		String logString = "Hobbit.move()";
		Logger.Log(1, logString, this);

		String answerText;
		boolean isSlowed;
		Obstacle obstacle;
		Cell cell;
		Scanner scanner = new Scanner (System.in);

		cell = this.getPosition();
		cell.getBusy();

		while(true) {
			System.out.print("Akadalyra lepett [igen, nem]: ");
			answerText = scanner.next();
			if(answerText.equals("igen")) {
				isSlowed=true;
				break;
			} else if(answerText.equals("nem")) {
				isSlowed=false;
				break;
			} else {
				System.out.println("Helytelen ertek");
				continue;
			}
		}

		if(isSlowed) {
			obstacle = cell.getObstacle();
			obstacle.getSlowRate();
			obstacle.getBonusSlowRates();
			this.setActualSpeed(1);
		} else {
			this.setActualSpeed(this.getOriginalSpeed());
		}

		Logger.Log(0, logString, this);
	}
}
